import { useState, useEffect } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import { HashRouter as Router } from "react-router-dom";
import { FaLinkedin, FaGithub, FaTwitter } from "react-icons/fa";
import { FcDocument } from "react-icons/fc";

export const NavBar = () => {

  const [activeLink, setActiveLink] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    }

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [])

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  }

  return (
    <Router>
      <Navbar expand="lg" className={scrolled ? "scrolled sticky-top" : "sticky-top"}>
        <Container fluid>
            <Navbar.Brand href="/">
      <FcDocument className="logo_navbar" />
    </Navbar.Brand>
        <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>

    <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav" style={{ flexWrap: "nowrap" }}>
      <Nav className="ms-auto">
        <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link>
        <Nav.Link href="#skills" className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>Skills</Nav.Link>
        <Nav.Link href="#projects" className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('projects')}>Projects</Nav.Link>
        <Nav.Link href="#connect" className={activeLink === 'connect' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('connect')}>Contact</Nav.Link>
      </Nav>
    </Navbar.Collapse>
    <span className="navbar-text">
      <div className="social-icon">
        <a href="https://linkedin.com/" id="linkedin-icon"><FaLinkedin /></a>
        <a href="https://github.com/" id="github-icon"><FaGithub /></a>
        <a href="https://twitter.com/" id="twitter-icon"><FaTwitter /></a>
      </div>
    </span>
    </div>
    {/* <Link to='#connect'>
      <button className="vvd"><span>Letâ€™s Connect</span></button>
    </Link> */}
  </Container>
</Navbar>

    </Router>
  )
}
